mapper.select:
	 select r.reservecode , h.housename , r.state , r.checkin , r.checkout from reservation r , house h
    where r.housecode = h.housecode
   and r.membercode = #{memberCode}
    and r.state= #{state}

	   select r.exreservecode , e.exname , r.state, r.exdate from exreserve r, experience e 
    where e.excode = r.excode
    and r.membercode = #{memberCode}
    and r.state= #{state}

select r.exreservecode , e.exname , r.exdate , r.state from experience e , exreserve r
where r.excode = e.excode
and r.membercode = #{memberCode}

select r.reservecode , e.housename , r.checkIn , r.checkout , r.state from house e ,reservation r
where r.housecode = e.housecode
and r.membercode=#{memberCode}
