mapper.select:

SELECT *
FROM (SELECT ROWNUM rnum, A.*
      FROM (SELECT * FROM house ORDER BY houseRegDate DESC) A)
WHERE rnum >= #{startRow}
  and rnum <= #{endRow}


SELECT *
FROM (SELECT ROWNUM rnum, A.*
      FROM (SELECT * FROM experience ORDER BY exRegDate DESC) A)
WHERE rnum >= #{startRow}
  and rnum <= #{endRow}

mapper.update:
update experience
set exApproval='승인완료'
where exCode = #{exCode}

update experience
set exApproval='승인거절'
where exCode = #{exCode}

update house
set APPROVALSTATUS='승인완료'
where houseCode = #{houseCode}

update house
set APPROVALSTATUS='승인거절'
where houseCode = #{houseCode}
