mapper.select:
select id
from member
where email = #{email}

select price
from house
where houseCode = #{houseCode}

select membercode
from house
where houseCode = #{houseCode}

select point
from member
where email = #{email}

select houseName
from house
where houseCode = #{houseCode}

select reserveCode
from RESERVATION
where houseCode = #{houseCode}
  and memberCode = #{memberCode}
  and checkIn = #{checkIn}

mapper.update:
update member
set point =#{memberPoint}
where id = #{memberCode}

mapper.select:
select reserveCode
from RESERVATION
where HOUSECODE = #{houseCode}
  and memberCode = #{memberCode}

select count(*)
from REVIEW
where reserveCode = #{reserveCode}
